$(document).ready(function() {
	$('#menu')
	  .sticky({
	    context: '#footer'
	  });
	$('.special.cards .image').dimmer({
  	on: 'hover'
  });
	$('.ui.card .image').dimmer({
  	on: 'hover'
  });
	$("#inside-menu > li .item").click(function() {
		$("#inside-menu li .item.active").removeClass('active');
		$(this).addClass("active");
	});
	$('.ui.sidebar').sidebar('toggle');
	$("#sidebarToggle").click(function() {
		$(".ui.sidebar").sidebar('toggle');
	});
	var lastscroll = 0;
	$(window).scroll(function() {
		var me = $(this).scrollTop();
		if(me > lastscroll){
			$("#mainNav").addClass("scroll-hidden");
		}else{
			$("#mainNav").removeClass("scroll-hidden");
		}
		lastscroll = me;
	});
	
});