$(document).ready(function() {
	$('#menu')
	  .sticky({
	    context: '#footer'
	  });
	$('.special.cards .image').dimmer({
  	on: 'hover'
  });
	$('.ui.card .image').dimmer({
  	on: 'hover'
  });
	$("#inside-menu > li .item").click(function() {
		$("#inside-menu li .item.active").removeClass('active');
		$(this).addClass("active");
	});
	$(".button-responsive").click(function() {
		$(".ui.right.fixed.vertical.menu").toggleClass("geser");
		$(".mask.all").addClass("addmask");
	});
	$("#button-close").click(function() {
		$(".ui.right.fixed.vertical.menu").removeClass("geser");
		$(".mask.all").removeClass("addmask");
	});
	$(".mask.all").click(function() {
		$(".ui.right.fixed.vertical.menu").removeClass("geser");
		$(this).removeClass("addmask");
	})
});